"""
Flask application for the ENT CPT Code Agent Web UI.
This file defines the Flask app and routes for interacting with the ENT CPT Agent.
"""

import os
import json
import logging
import sys
from pathlib import Path
from flask import Flask, render_template, request, jsonify, redirect, url_for

# Configure path to ensure imports work correctly
sys.path.insert(0, str(Path(__file__).resolve().parents[3]))  # Add project root to path

# Import agent components
from src.config.agent_config import AgentConfig
from src.conversation.conversation_manager import ConversationManager
from src.agent.ent_cpt_agent import ENTCPTAgent

# Configure logging
logging.basicConfig(
    level=logging.INFO,
    format='%(asctime)s - %(name)s - %(levelname)s - %(message)s',
    handlers=[logging.StreamHandler()]
)
logger = logging.getLogger("ent_cpt_agent_web")

# Initialize Flask app with correct template folder
template_dir = os.path.dirname(os.path.abspath(__file__))
app = Flask(__name__, template_folder=template_dir)

# Global variables for agent and configuration
config = None
agent = None
initialization_error = None
agent_initialized = False

# Initialize the agent function
def init_agent():
    """Initialize the agent with configuration."""
    global config, agent, initialization_error, agent_initialized

    # Skip if already initialized
    if agent_initialized:
        return True

    try:
        logger.info("Starting agent initialization")

        # Initialize configuration
        config_path = os.environ.get("CONFIG_PATH", "config.json")
        logger.info(f"Loading configuration from {config_path}")
        config = AgentConfig(config_path)

        # Initialize conversation manager
        conversation_dir = config.get("agent", "conversation_dir")
        logger.info(f"Initializing conversation manager with directory: {conversation_dir}")
        conversation_manager = ConversationManager(conversation_dir)

        # Initialize agent
        logger.info("Initializing ENT CPT Agent")
        agent = ENTCPTAgent(config, conversation_manager)
        logger.info("Agent initialized successfully")
        initialization_error = None
        agent_initialized = True
        return True
    except Exception as e:
        logger.error(f"Error initializing agent: {e}", exc_info=True)
        initialization_error = str(e)
        agent = None
        agent_initialized = False
        return False

# Attempt to initialize the agent at startup
init_agent()

# Define a before_request handler for newer Flask versions
@app.before_request
def ensure_agent_initialized():
    """Ensure the agent is initialized before handling any request."""
    if not agent_initialized and not request.endpoint == 'static':
        init_agent()

@app.route('/')
def index():
    """Render the main page."""
    # Check if agent is initialized
    if agent is None:
        # Return an error page if agent initialization failed
        return render_template('error.html', 
                              error="Agent initialization failed", 
                              details=initialization_error)

    try:
        # Generate a new session ID
        conversation = agent.conversation_manager.create_conversation()
        session_id = conversation.session_id

        return render_template('index.html', session_id=session_id)
    except Exception as e:
        logger.error(f"Error creating conversation: {e}", exc_info=True)
        return render_template('error.html', 
                               error="Failed to create conversation", 
                               details=str(e))

@app.route('/api/query', methods=['POST'])
def query_api():
    """API endpoint for querying the agent."""
    # Check if agent is initialized
    if agent is None:
        return jsonify({"status": "error", "message": "Agent not initialized"}), 500

    try:
        data = request.json
        query = data.get('query')
        session_id = data.get('session_id')

        if not query:
            return jsonify({"status": "error", "message": "No query provided"})

        # Get or create session
        conversation_manager = agent.conversation_manager

        if session_id and conversation_manager.get_conversation(session_id):
            conversation = conversation_manager.get_conversation(session_id)
        else:
            conversation = conversation_manager.create_conversation()
            session_id = conversation.session_id

        # Add user message to conversation
        conversation.add_message("user", query)

        # Process the query
        response = agent.process_query(query, conversation)

        # Extract CPT codes from response
        codes = conversation_manager.extract_cpt_codes(response)

        # Add assistant message to conversation
        conversation.add_message("assistant", response, codes)

        # Save conversation
        conversation_manager.save_conversation(conversation)

        return jsonify({
            "status": "success",
            "message": response,
            "data": {
                "codes": codes
            },
            "session_id": session_id
        })

    except Exception as e:
        logger.error(f"Error processing query: {e}", exc_info=True)
        return jsonify({"status": "error", "message": str(e)}), 500

@app.route('/api/search', methods=['POST'])
def search_codes():
    """API endpoint for searching CPT codes."""
    # Check if agent is initialized
    if agent is None:
        return jsonify({"status": "error", "message": "Agent not initialized"}), 500

    try:
        data = request.json
        search_term = data.get('search_term')

        if not search_term:
            return jsonify({"status": "error", "message": "No search term provided"})

        results = agent.cpt_db.search_codes(search_term)

        return jsonify({
            "status": "success",
            "data": {
                "codes": results,
                "count": len(results)
            }
        })

    except Exception as e:
        logger.error(f"Error searching codes: {e}", exc_info=True)
        return jsonify({"status": "error", "message": str(e)}), 500

@app.route('/api/validate', methods=['POST'])
def validate_code():
    """API endpoint for validating CPT codes."""
    # Check if agent is initialized
    if agent is None:
        return jsonify({"status": "error", "message": "Agent not initialized"}), 500

    try:
        data = request.json
        code = data.get('code')

        if not code:
            return jsonify({"status": "error", "message": "No code provided"})

        result = agent.cpt_db.get_code_validation(code)

        return jsonify({
            "status": "success" if result.get("valid", False) else "error",
            "message": result.get("description") if result.get("valid", False) else result.get("error"),
            "data": result
        })

    except Exception as e:
        logger.error(f"Error validating code: {e}", exc_info=True)
        return jsonify({"status": "error", "message": str(e)}), 500

@app.route('/api/analyze', methods=['POST'])
def analyze_procedure():
    """API endpoint for analyzing a procedure description."""
    # Check if agent is initialized
    if agent is None:
        return jsonify({"status": "error", "message": "Agent not initialized"}), 500

    try:
        data = request.json
        procedure_text = data.get('procedure_text')
        candidate_codes = data.get('candidate_codes')

        if not procedure_text:
            return jsonify({"status": "error", "message": "No procedure text provided"})

        # If candidate codes weren't provided, search for them
        if not candidate_codes:
            search_results = agent.cpt_db.search_codes(procedure_text)
            candidate_codes = [result["code"] for result in search_results]

        # Analyze the procedure using the rules engine
        analysis = agent.rules_engine.analyze_procedure(
            procedure_text, candidate_codes, agent.cpt_db)

        return jsonify({
            "status": "success",
            "data": analysis
        })

    except Exception as e:
        logger.error(f"Error analyzing procedure: {e}", exc_info=True)
        return jsonify({"status": "error", "message": str(e)}), 500

@app.route('/api/new_session', methods=['POST'])
def new_session():
    """Create a new conversation session."""
    # Check if agent is initialized
    if agent is None:
        return jsonify({"status": "error", "message": "Agent not initialized"}), 500

    try:
        conversation = agent.conversation_manager.create_conversation()

        return jsonify({
            "status": "success",
            "message": "New session created",
            "session_id": conversation.session_id
        })

    except Exception as e:
        logger.error(f"Error creating new session: {e}", exc_info=True)
        return jsonify({"status": "error", "message": str(e)}), 500

@app.route('/api/health', methods=['GET'])
def health_check():
    """Health check endpoint to verify the API is working."""
    try:
        # Check if agent is initialized
        if agent is None:
            return jsonify({
                "status": "warning",
                "message": "Agent not initialized",
                "details": initialization_error
            })

        # Check if database is loaded
        if not hasattr(agent, 'cpt_db') or not agent.cpt_db:
            return jsonify({
                "status": "warning",
                "message": "CPT database not loaded"
            })

        # Check if model is initialized
        if not hasattr(agent, 'model') or not agent.model:
            return jsonify({
                "status": "warning",
                "message": "LM Studio model not initialized"
            })

        return jsonify({
            "status": "success",
            "message": "Service is healthy",
            "details": {
                "model": agent.model_name,
                "database": agent.cpt_db_path,
                "conversation_dir": agent.conversation_manager.conversation_dir
            }
        })

    except Exception as e:
        logger.error(f"Error in health check: {e}", exc_info=True)
        return jsonify({"status": "error", "message": str(e)}), 500

# Debug endpoint to help diagnose issues
@app.route('/debug', methods=['GET'])
def debug_info():
    """Debug endpoint to get information about the application state."""
    return jsonify({
        "agent_initialized": agent_initialized,
        "agent_is_none": agent is None,
        "initialization_error": initialization_error,
        "has_config": config is not None,
        "template_dir": template_dir,
        "cwd": os.getcwd(),
        "python_path": sys.path,
        "environment_variables": {k: v for k, v in os.environ.items() if k.startswith(("CONFIG", "WEB", "DEBUG"))}
    })

if __name__ == '__main__':
    """
    This section only executes when the file is run directly.
    In production, use the run_web_ui.py script instead.
    """
    port = int(os.environ.get("WEB_PORT", "5000"))
    host = os.environ.get("WEB_HOST", "0.0.0.0")
    debug = os.environ.get("DEBUG", "False").lower() == "true"

    # Try to initialize the agent again if needed
    if not agent_initialized:
        init_agent()

    print(f"Starting web server on {host}:{port} (debug={debug})")
    app.run(host=host, port=port, debug=debug)

"""
Main module for the ENT CPT Agent.
Contains the ENTCPTAgent class for processing ENT procedure queries.
"""

import os
import pandas as pd
import re
import json
import lmstudio as lms
import logging
from typing import List, Dict, Any, Optional, Tuple
from pathlib import Path
from openai import OpenAI

# Configure logging
logging.basicConfig(
    level=logging.INFO,
    format='%(asctime)s - %(name)s - %(levelname)s - %(message)s',
    handlers=[logging.StreamHandler()]
)
logger = logging.getLogger("ent_cpt_agent")

class CPTCodeDatabase:
    """
    Handles loading, processing, and querying of CPT codes for ENT procedures.
    """
    def __init__(self, file_path: str):
        """
        Initialize the CPT code database from the provided Excel file.

        Args:
            file_path: Path to the Excel file containing CPT codes
        """
        self.file_path = file_path
        self.df = None
        self.code_descriptions = {}
        self.code_categories = {}
        self.related_codes = {}
        self.load_data()

    def load_data(self) -> None:
        """Load CPT code data from Excel file and process it."""
        logger.info(f"Loading CPT codes from {self.file_path}")
        try:
            self.df = pd.read_excel(self.file_path)

            # Process the dataframe to create lookup dictionaries
            for _, row in self.df.iterrows():
                code = str(row.get('CPT Code', '')).strip()
                if code and not pd.isna(code):
                    # Store description
                    self.code_descriptions[code] = row.get('Description', '')

                    # Store category
                    category = row.get('Category', '')
                    if category and not pd.isna(category):
                        if category not in self.code_categories:
                            self.code_categories[category] = []
                        self.code_categories[category].append(code)

                    # Store related codes
                    related = row.get('Related Codes', '')
                    if related and not pd.isna(related):
                        related_codes = [r.strip() for r in str(related).split(',')]
                        self.related_codes[code] = related_codes

            logger.info(f"Loaded {len(self.code_descriptions)} CPT codes")
        except Exception as e:
            logger.error(f"Error loading CPT codes: {e}")
            raise

    def search_codes(self, query: str) -> List[Dict[str, Any]]:
        """
        Search for CPT codes based on a text query.

        Args:
            query: Search terms for finding relevant CPT codes

        Returns:
            List of matching CPT codes with descriptions
        """
        query = query.lower()
        results = []

        for code, description in self.code_descriptions.items():
            if query in description.lower() or query in code:
                results.append({
                    "code": code,
                    "description": description,
                    "related_codes": self.related_codes.get(code, [])
                })

        return results

    def get_code_details(self, code: str) -> Dict[str, Any]:
        """
        Get detailed information about a specific CPT code.

        Args:
            code: The CPT code to look up

        Returns:
            Dictionary containing detailed information about the code
        """
        if code not in self.code_descriptions:
            return {"error": f"CPT code {code} not found"}

        return {
            "code": code,
            "description": self.code_descriptions.get(code, ""),
            "related_codes": self.related_codes.get(code, [])
        }

    def get_codes_by_category(self, category: str) -> List[Dict[str, Any]]:
        """
        Get all CPT codes belonging to a specific category.

        Args:
            category: The category to look up codes for

        Returns:
            List of CPT codes in the specified category
        """
        if category not in self.code_categories:
            return []

        results = []
        for code in self.code_categories[category]:
            results.append({
                "code": code,
                "description": self.code_descriptions.get(code, ""),
                "related_codes": self.related_codes.get(code, [])
            })

        return results

    def get_code_validation(self, code: str) -> Dict[str, Any]:
        """
        Validate if a CPT code exists and is valid.

        Args:
            code: The CPT code to validate

        Returns:
            Dictionary with validation results
        """
        if code in self.code_descriptions:
            return {"valid": True, "description": self.code_descriptions[code]}
        else:
            return {"valid": False, "error": f"Invalid CPT code: {code}"}

class RulesEngine:
    """Simple rules engine for CPT code analysis."""

    def get_coding_tips(self, code: str, description: str) -> List[str]:
        """Get coding tips for a CPT code."""
        tips = [
            "Ensure documentation supports all components of the procedure.",
            "Check if any modifiers are needed (e.g., bilateral -50).",
            "Verify this is the most specific code available.",
            "Consider whether any bundled services are included.",
            "Confirm medical necessity is documented."
        ]

        if "endoscopic" in description.lower():
            tips.append("For endoscopic procedures, document approach and findings clearly.")

        if "biopsy" in description.lower():
            tips.append("For biopsies, include documentation of collection method and site.")

        return tips

    def analyze_procedure(self, procedure_text: str, candidate_codes: List[str], 
                         cpt_db: CPTCodeDatabase) -> Dict[str, Any]:
        """Analyze a procedure to determine appropriate CPT codes."""
        result = {
            "status": "success",
            "recommended_codes": [],
            "excluded_codes": [],
            "explanations": []
        }

        procedure_lower = procedure_text.lower()

        for code in candidate_codes:
            code_info = cpt_db.get_code_details(code)
            if "error" in code_info:
                continue

            description = code_info["description"].lower()
            match_score = 0

            for word in description.split():
                if word in procedure_lower and len(word) > 3:
                    match_score += 1

            if match_score >= 2:
                result["recommended_codes"].append(code)
                result["explanations"].append({
                    "code": code,
                    "message": f"Code {code} matches key terms in the procedure description."
                })
            else:
                result["excluded_codes"].append(code)

        if "bilateral" in procedure_lower:
            result["recommended_codes"] = [
                f"{code}-50" if not code.endswith("-50") else code 
                for code in result["recommended_codes"]
            ]
            result["explanations"].append({
                "message": "Added -50 modifier for bilateral procedure."
            })

        return result

class ENTCPTAgent:
    """
    Agent for processing ENT procedure queries and determining appropriate CPT codes.
    """
    def __init__(self, config, conversation_manager=None):
        """
        Initialize the ENT CPT Agent.

        Args:
            config: AgentConfig object with configuration settings
            conversation_manager: Optional ConversationManager instance
        """
        self.config = config

        # Get configuration values
        self.model_name = config.get("model", "name")
        self.model_temperature = config.get("model", "temperature")
        self.model_max_tokens = config.get("model", "max_tokens")
        self.cpt_db_path = config.get("cpt_database", "file_path")

        # Initialize components
        self.cpt_db = CPTCodeDatabase(self.cpt_db_path)
        self.rules_engine = RulesEngine()
        self.conversation_manager = conversation_manager

        # Initialize OpenAI client for LM Studio
        server_config = config.get("server")
        self.client = OpenAI(
            base_url=server_config.get("lm_studio_base_url", "http://localhost:1234/v1"),
            api_key=server_config.get("lm_studio_api_key", "lm-studio")
        )

        # Define system prompt and tools
        self.system_prompt = self._create_system_prompt()
        self.tools = self._define_tools()

    def _create_system_prompt(self) -> str:
        """Create the system prompt for the agent."""
        return """
        You are an expert ENT (Ear, Nose, Throat) medical coding assistant specialized in CPT codes. 
        Your goal is to help medical professionals find the correct CPT codes for ENT procedures.

        You have access to tools that can search and validate CPT codes. Use these tools
        to provide accurate coding assistance.

        When recommending CPT codes:
        - Always verify that the procedure description matches the code exactly
        - Check for any modifiers that may be needed (e.g., bilateral procedures)
        - Explain why you're recommending specific codes
        - Consider bundling rules and related codes
        - Format CPT codes clearly using the format: XXXXX (description)

        Always provide educational explanations of your reasoning process so
        medical professionals can learn the correct coding principles.
        """

    def _define_tools(self) -> List[Dict[str, Any]]:
        """Define the tools that the model can use."""
        return [
            {
                "type": "function",
                "function": {
                    "name": "search_cpt_codes",
                    "description": "Search for CPT codes based on a procedure description or keywords",
                    "parameters": {
                        "type": "object",
                        "properties": {
                            "query": {
                                "type": "string",
                                "description": "The procedure description or keywords to search for"
                            }
                        },
                        "required": ["query"]
                    }
                }
            },
            {
                "type": "function",
                "function": {
                    "name": "validate_cpt_code",
                    "description": "Validate if a CPT code exists and is correct",
                    "parameters": {
                        "type": "object",
                        "properties": {
                            "code": {
                                "type": "string",
                                "description": "The CPT code to validate"
                            }
                        },
                        "required": ["code"]
                    }
                }
            },
            {
                "type": "function",
                "function": {
                    "name": "get_category_codes",
                    "description": "Get all CPT codes for a specific ENT category",
                    "parameters": {
                        "type": "object",
                        "properties": {
                            "category": {
                                "type": "string",
                                "description": "The ENT category to get codes for"
                            }
                        },
                        "required": ["category"]
                    }
                }
            },
            {
                "type": "function",
                "function": {
                    "name": "explain_coding_rules",
                    "description": "Explain the rules for using a specific CPT code",
                    "parameters": {
                        "type": "object",
                        "properties": {
                            "code": {
                                "type": "string",
                                "description": "The CPT code to explain rules for"
                            }
                        },
                        "required": ["code"]
                    }
                }
            },
            {
                "type": "function",
                "function": {
                    "name": "analyze_procedure",
                    "description": "Analyze a procedure description to determine appropriate CPT codes",
                    "parameters": {
                        "type": "object",
                        "properties": {
                            "procedure_text": {
                                "type": "string",
                                "description": "Description of the ENT procedure"
                            }
                        },
                        "required": ["procedure_text"]
                    }
                }
            }
        ]

    def search_cpt_codes(self, query: str) -> str:
        """Search for CPT codes based on a procedure description."""
        results = self.cpt_db.search_codes(query)
        if not results:
            return f"No CPT codes found matching '{query}'"

        response = f"Found {len(results)} CPT codes matching '{query}':\n\n"
        for idx, result in enumerate(results, 1):
            response += f"{idx}. Code {result['code']}: {result['description']}\n"
            if result['related_codes']:
                response += f"   Related codes: {', '.join(result['related_codes'])}\n"

        return response

    def validate_cpt_code(self, code: str) -> str:
        """Validate if a CPT code exists and is correct."""
        result = self.cpt_db.get_code_validation(code)
        if result["valid"]:
            return f"CPT code {code} is valid: {result['description']}"
        else:
            return result["error"]

    def get_category_codes(self, category: str) -> str:
        """Get all CPT codes for a specific ENT category."""
        results = self.cpt_db.get_codes_by_category(category)
        if not results:
            return f"No CPT codes found for category '{category}'"

        response = f"CPT codes for category '{category}':\n\n"
        for idx, result in enumerate(results, 1):
            response += f"{idx}. Code {result['code']}: {result['description']}\n"

        return response

    def explain_coding_rules(self, code: str) -> str:
        """Explain the rules for using a specific CPT code."""
        result = self.cpt_db.get_code_details(code)
        if "error" in result:
            return result["error"]

        response = f"Coding guidelines for CPT {code}:\n\n"
        response += f"Description: {result['description']}\n\n"

        tips = self.rules_engine.get_coding_tips(code, result['description'])

        response += "Coding guidelines:\n"
        for idx, tip in enumerate(tips, 1):
            response += f"{idx}. {tip}\n"

        if result['related_codes']:
            response += f"\nRelated codes to consider: {', '.join(result['related_codes'])}\n"
            response += "\nAlways check if one of these related codes may be more appropriate based on specific procedure details."

        return response

    def analyze_procedure(self, procedure_text: str) -> str:
        """Analyze a procedure description to determine appropriate CPT codes."""
        search_results = self.cpt_db.search_codes(procedure_text)
        candidate_codes = [result["code"] for result in search_results]

        if not candidate_codes:
            return f"No potential CPT codes found for this procedure description. Please provide more details about the procedure."

        analysis = self.rules_engine.analyze_procedure(
            procedure_text, candidate_codes, self.cpt_db)

        if analysis["status"] != "success":
            return f"Error analyzing procedure: {analysis.get('message', 'Unknown error')}"

        response = f"Analysis of procedure: {procedure_text}\n\n"

        response += "Recommended CPT codes:\n"
        for code in analysis["recommended_codes"]:
            code_info = self.cpt_db.get_code_details(code.split('-')[0])
            description = code_info.get("description", "Unknown") if "error" not in code_info else "Unknown"
            response += f"- {code}: {description}\n"

        if analysis["explanations"]:
            response += "\nRecommendation details:\n"
            for explanation in analysis["explanations"]:
                response += f"- {explanation['message']}\n"

        if analysis["excluded_codes"]:
            response += "\nExcluded codes (may be bundled or inappropriate):\n"
            for code in analysis["excluded_codes"]:
                code_info = self.cpt_db.get_code_details(code)
                description = code_info.get("description", "Unknown") if "error" not in code_info else "Unknown"
                response += f"- {code}: {description}\n"

        return response

    def execute_tool(self, tool_name: str, arguments: Dict[str, Any]) -> str:
        """Execute a tool function based on name and arguments."""
        try:
            if tool_name == "search_cpt_codes":
                return self.search_cpt_codes(arguments["query"])
            elif tool_name == "validate_cpt_code":
                return self.validate_cpt_code(arguments["code"])
            elif tool_name == "get_category_codes":
                return self.get_category_codes(arguments["category"])
            elif tool_name == "explain_coding_rules":
                return self.explain_coding_rules(arguments["code"])
            elif tool_name == "analyze_procedure":
                return self.analyze_procedure(arguments["procedure_text"])
            else:
                return f"Unknown tool: {tool_name}"
        except Exception as e:
            logger.error(f"Error executing tool {tool_name}: {e}")
            return f"Error executing {tool_name}: {str(e)}"

    def process_query(self, query: str, conversation=None) -> str:
        """
        Process a user query about ENT procedures and CPT codes.

        Args:
            query: The user's question or procedure description
            conversation: Optional Conversation object

        Returns:
            Agent's response with CPT code recommendations
        """
        logger.info(f"Processing query: {query}")

        # Initialize messages for OpenAI API
        messages = []
        messages.append({"role": "system", "content": self.system_prompt})

        # Add conversation history if provided
        if conversation and hasattr(conversation, 'messages'):
            for msg in conversation.messages:
                if msg["role"] in ["user", "assistant"]:
                    messages.append({"role": msg["role"], "content": msg["content"]})

        # Add the current query
        messages.append({"role": "user", "content": query})

        try:
            # Call the model
            response = self.client.chat.completions.create(
                model=self.model_name,
                messages=messages,
                tools=self.tools,
                temperature=self.model_temperature,
                max_tokens=self.model_max_tokens
            )

            # Check if the model wants to use tools
            if hasattr(response.choices[0].message, 'tool_calls') and response.choices[0].message.tool_calls:
                # Process all tool calls
                tool_calls = response.choices[0].message.tool_calls

                # Add assistant message with tool calls to conversation
                messages.append({
                    "role": "assistant",
                    "tool_calls": [
                        {
                            "id": tool_call.id,
                            "type": tool_call.type,
                            "function": {
                                "name": tool_call.function.name,
                                "arguments": tool_call.function.arguments
                            }
                        }
                        for tool_call in tool_calls
                    ]
                })

                # Process each tool call
                for tool_call in tool_calls:
                    # Parse arguments
                    args = json.loads(tool_call.function.arguments)

                    # Execute the tool
                    result = self.execute_tool(tool_call.function.name, args)

                    # Add tool response to conversation
                    messages.append({
                        "role": "tool",
                        "tool_call_id": tool_call.id,
                        "content": result
                    })

                # Generate final response after tool calls
                final_response = self.client.chat.completions.create(
                    model=self.model_name,
                    messages=messages,
                    temperature=self.model_temperature,
                    max_tokens=self.model_max_tokens
                )

                # Add final assistant response to conversation history if provided
                final_content = final_response.choices[0].message.content
                if conversation and hasattr(conversation, 'add_message'):
                    conversation.add_message("assistant", final_content)

                # Return the final response
                return final_content
            else:
                # No tool calls, just return the direct response
                content = response.choices[0].message.content

                # Add response to conversation history if provided
                if conversation and hasattr(conversation, 'add_message'):
                    conversation.add_message("assistant", content)

                return content

        except Exception as e:
            logger.error(f"Error processing query: {e}", exc_info=True)
            return f"I encountered an error while processing your query: {str(e)}"

    def extract_cpt_codes(self, text: str) -> List[str]:
        """Extract CPT codes from text."""
        pattern = r'\b\d{5}(?:-\d{1,2})?\b'
        matches = re.findall(pattern, text)
        return matches

